import React, { Component } from "react";

const Tasks = (props) =>{

    const {items , deletItem} = props;
    const listItem =items.map(item =>{

        return(<div key={item.id}>
            <div class="task">
          <span class="icon-star icon" 
          onClick={() => startItem(item.id)}> </span>
          <p  class="task-text">{item.task}</p>
    
          <div>
            <span class="icon-trash icon" 
            onClick={() => deletItem(item.id)}> </span>
    
            <span className="icon-file_done icon" > </span>
          </div> 
         </div>
        </div>
        )
    })
  
    return(
      <div>
          {listItem}
      </div>
      
    );
   
}

export default Tasks
