    }
    
    const handleComplete =(todo)=>{
        setTodos(
            todos.map((item)=>{
                if(item.id === todo.id){
                    console.log(item)
                    return{
                        ...item,completed :!item.completed
                     
                    }
                } 
                return item;
            })
        )
    }

   const handleEdit =({id}) =>{
