import React, { Component } from "react";
class Add extends Component{
    state = {
        task:''
    }
   
    hancdleChange = (e)  =>{
        this.setState({task : e.target.value})   
    }
  
    handlesubmit =(e) =>{
        e.preventDefault();
        this.props.addItem(this.state) 
    };

    render(){
    return(
        <div>
        
        <form>

          <input   lang="ar"  type="text"  
          placeholder="Task to be done.."  required id="task"
          onChange={this.hancdleChange}/>
          <button class="icon-plus" onClick={this.handlesubmit}>
          </button>
        </form>

        </div>
    );
    }
}
export default Add
