import axios from "axios";
import React from "react";

const Tasks = ({todos , setTodos , setEditTodo }) =>{

    const handleDelete=({id})=>{
        setTodos(todos.filter((todo) => todo.id !== id));
        axios.delete('http://localhost:5000/Tasks/'+id)
        .then(response => console.log('daleted'))
    }
    
    var comp = false ;

    const handleComplete =(todo)=>{
        setTodos(
            todos.map((item)=>{
                if(item.id === todo.id){
                    console.log(todo.statue)
                   if(todo.statue === "not yet"){
                      comp=true
                      return {...item.statue="done"}   
                   }
                      comp = false                     
                      return {...item.statue="not yet"} 

                   }
                   
                return item;
                } 
                
            )
        )
        }

   const handleEdit =({id}) =>{
       const findTodo = todos.find((todo) => todo.id ===id);
       setEditTodo(findTodo);
   }
   
    return(
    <div>

      {todos.map((todo)=>(
 
           <div className="task" key={todo.id}>
   
               <button className="buttonEdit" onClick={()=>handleEdit(todo)}><h6>Edit</h6></button>
              
               <input className={`list ${comp ? "finish" :""}`}
               type="text"
               value={todo.details}
               onChange={(event) => event.preventDefault()}
               />
          <div>
                 <span className="icon-trash icon"  onClick={() =>handleDelete(todo)}> </span>
            
                 <span className="icon-file_done icon" onClick={() =>handleComplete(todo)}> </span>
          </div> 
          </div>
 
      )
      )}
   
    </div>
      
    );
   
}

export default Tasks
