import React from "react";

const Tasks = ({todos , setTodos}) =>{

    const handleDelete=({id})=>{
        setTodos(todos.filter((todo) => todo.id !== id));
    }
    
    const handleComplete =(todo)=>{
        setTodos(
            todos.map((item)=>{
                if(item.id === todo.id){
                    return
                } 

            })
        )
    }

    return(
    <div>

      {todos.map((todo)=>(
 
           <div class="task" key={todo.id}>
   
               <span class="icon-star icon" >     </span>
              
               <input className="list" type="text"
               value={todo.task}
               onChange={(event) => event.preventDefault()}
   
               />
          <div>
                 <span class="icon-trash icon"  onClick={() =>handleDelete(todo)}> </span>
            
                 <span className="icon-file_done icon" onClick={() =>handleComplete(todo)}> </span>
            </div> 
            </div>
 
      )
      )}
   
    </div>
      
    );
   
}

export default Tasks
